/**
 * Animation class is for general animations within the application
 * The GeneralAnimation class is a singleton class,
 * meaning that there is at most 1 instance of the class available
 * */
import $ from 'jquery';

let animationInstance = null;

class LoadingAnimation {
    constructor(){
        if(animationInstance){
            return animationInstance;
        }
        /** Class parameters*/
        this.$loader = null;
        this.$content = null;
        this.updateCache();
    }
    /**
     * Update/save the cache of the dom
     * */
    updateCache(){
        this.$loader = $('#loader');
        this.$content = $('#main-content');
    }

    loadingAnimation(activate) {
        //If cache is not available
        if(this.$loader == null || this.$content == null){
            this.updateCache();
        }
        //Turn on the animation
        if (activate === true) {
            this.$content.addClass('hide');
            this.$loader.removeClass('hide');
        } else {//Turn off the animation unhide the content
            this.$content.removeClass('hide');
            this.$loader.addClass('hide');
        }
    }
}

export default LoadingAnimation;

/**
 * This class is responsible for animating elements that have a class attribute "shakable".
 * When an element is clicked with class "shakable", class "shake" is added to its attributes.
 * After the animation is done the "shake" class is removed.
 * In HTML use the class "shakable" for each element that shake property
 * @Example <div class="shakable"></div>
 * */

import $ from 'jquery';

export default class ShakeAnimation{

    constructor(){
        this.animationEvent = 'webkitAnimationEnd oanimationend msAnimationEnd animationend';
        this.bindUIActions();
    }

    /**
     * Make all the actions connected to this module
     * return {void}
     * */
    bindUIActions(){
        var _this = this;
        $('.shakable').click((event) => {this.shakeEvent($(event.target));});
    }

    /**
     * Use the clicked element to invoke shakeElement
     * @return {void}
     * */
    shakeEvent(elem){
        this.shakeElement(elem);
    }

    /**
     * Function adds class shake, after the animation is done,
     * the class shake is removed from the element
     * @param {jQuery element}, elem
     * @return {void}
     * */
    shakeElement(elem){
        elem.addClass('shake wrongAlert');
        elem.one(this.animationEvent, function(event) {
            elem.removeClass('shake wrongAlert')
        });
    }

    /**
     * Shakes the element that has the focus
     * @return {void}
     * */
    shakeFocusedElement(){
        this.shakeElement($(document.activeElement));
    }

    /**
     * Make the given element shakable
     * @param {Object}, element
     * @return {void}
    * */
    makeShakable(elem){
        elem.addClass('shakable');
        this.bindUIActions();
    }

    /**
     * Make the given element non shakable
     * @param {Object}, element
     * @return {void}
     * */
    makeNonShakable(elem){
        elem.removeClass('shakable');
        this.bindUIActions();
    }
}

/**
 * Application Starting point
 * Rounters and Handles defined here
 */
import page from "page";
import Generator from './generator';
import Starter from './pages/starter';
import NotFound from './pages/not_found';
import Events from './pubsub';

/********************** Routes **************************/
page('/', index);
page('/get-ex', getEx);
page('/practice/:practicePlan', practice);
page('*', notFound);

/******************* Page Settings **********************/
page.exit('*', backFunction);
page({hashbang:true});
page.start();

/********************** Handlers ***********************/
/**
 * Main starter screen route
 * */
function index() {
    window.onload = new Starter();
}

/**
 * Get-ex route
 * */
function getEx() {
    //For now hand codded exercise generator
    window.onload = new Generator([[1,5],[2,3]]);
}

/**
 * Practice route
 * Each exercise card set has its own index
 * @example http://127.0.0.1:5000/#!/practice/0 , practice plan with index 0
 * */
function practice(ctx) {
    window.onload = new Generator(Starter.prototype.exNames[ctx.params.practicePlan].exID);
}

/**
 * When leaving a page, perform the following action
 * Reset all events from pubsub module to prevent interception from past subscribers
 * */
function backFunction(ctx,next) {
    Events.resetAll();
    next();
}

/**
 * Not found page
 * */
function notFound(){
    window.onload = new NotFound();
}

