/** Modular progress bar @author Martin Avagyan
 *  Initialize it using ProgressBar.init(percent,size);
 *  Add progress using  ProgressBar.move();
**/
import events from './pubsub';

var bar,ProgressBar = {
	settings:{
		percent: 0, //default starting point
		amount: 25, //default value for progress amount
		elem: 0,
	},	
	init: function(percent,size){	
		this.bindUIActions();		
		bar = this.settings;
		bar.percent = percent;
		bar.amount = 100/size;
		bar.elem = document.getElementById("ex-bar");
		bar.elem.style.width = bar.percent;
		// "bind" event
		events.on('progress', this.move);
	},	
	restart: function(){
		bar.percent = 0;
		bar.elem.style.width = bar.percent;
	},	
	bindUIActions: function() {},	
	move: function() {  
		  var width = bar.percent;
		  var id = setInterval(frame, 10);
		  var max_move = bar.percent+bar.amount;
		  function frame() {
			if (width >= max_move || width >=100) {
			  clearInterval(id);
			} else {
			  width++; 			  
			  bar.elem.style.width = width + '%'; 
			}
			bar.percent = max_move;
		}
	},
	terminate: function () {
		events.off('progress', this.move);
	},
};

export default ProgressBar;

/**
 * Event listener implemented using publish subscribe pattern
 * There is only one object of this class through application
 * */
var events = (function() {
    var events = {};
    var debugging = false;

    function on(eventName, fn) {
        events[eventName] = events[eventName] || [];
        events[eventName].push(fn);
        debug("ON: ");
    }
    function off(eventName, fn) {
        if (events[eventName]) {
            for (var i = 0; i < events[eventName].length; i++) {
                if( events[eventName][i] === fn ) {
                    events[eventName].splice(i, 1);
                    break;
                }
            }
        }
        debug("OFF: ");
    }
    function emit(eventName, data) {
        if (events[eventName]) {
            events[eventName].forEach(function(fn) {
                fn(data);
            });
        }
        debug("EMIT: ");
    }
    function debug(msg){
        if(!debugging) return;

        console.info(msg);
        console.info(events);
    }
    function resetAll() {
        events = {};
        debug("Reset All: ");
    }
    function removeEvent(eventName) {
        delete events[eventName];
    }
    return {
        on: on,
        off: off,
        emit: emit,
        resetAll: resetAll,
    };

})();

export default events;

